import React, { useState } from 'react';
import COMPANY_DEPT from '../data/companyDepartments';
import US_STATES from '../data/usStates';
import DatePicker from 'react-datepicker';
import Modal from 'react-modal';

import 'react-datepicker/dist/react-datepicker.css';
import '../styles/create-employee.css';

const RegisterForm = () => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [dateOfBirth, setdateOfBirth] = useState(new Date());
    const [startDate, setStartDate] = useState(new Date());
    const [street, setStreet] = useState('');
    const [city, setCity] = useState('');
    const [state, setState] = useState('');
    const [zipCode, setZipCode] = useState('');
    const [department, setDepartment] = useState('');
    const [modalIsOpen, setIsOpen] = React.useState(false);

    function openModal() {
        setIsOpen(true);
    }

    function afterOpenModal() {
        subtitle.style.color = '#f00';
    }

    function closeModal() {
        setIsOpen(false);
    }

    const submitForm = (e) => {
        e.preventDefault();
        const EmployeesList =
            JSON.parse(localStorage.getItem('EmployeesList')) || [];
        const Employee = {
            firstName,
            lastName,
            dateOfBirth: dateOfBirth.toLocaleDateString('en-US'),
            startDate: startDate.toLocaleDateString('en-US'),
            street,
            city,
            state,
            zipCode,
            department,
        };
        EmployeesList.push(Employee);
        localStorage.setItem('EmployeesList', JSON.stringify(EmployeesList));
    };

    return (
        <>
            <div>
                <form onSubmit={submitForm} className="form">
                    <label htmlFor="firstName">First Name</label>
                    <input
                        type="text"
                        id="firstName"
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                        required
                    />
                    <label htmlFor="lastName">Last Name</label>
                    <input
                        type="text"
                        id="lastName"
                        value={lastName}
                        onChange={(e) => setLastName(e.target.value)}
                        required
                    />
                    <label htmlFor="dateOfBirth">Date of Birth</label>
                    <DatePicker
                        type="date"
                        id="birthdate"
                        selected={dateOfBirth}
                        onChange={(date) => setdateOfBirth(date)}
                    />
                    <label htmlFor="startDate">Start Date</label>
                    <DatePicker
                        type="date"
                        id="startDate"
                        selected={startDate}
                        onChange={(date) => setStartDate(date)}
                    />
                    <fieldset className="address-insert">
                        <legend className="address-title">Address</legend>
                        <label htmlFor="street">Street</label>
                        <input
                            type="text"
                            id="street"
                            value={street}
                            onChange={(e) => setStreet(e.target.value)}
                            required
                        />
                        <label htmlFor="city">City</label>
                        <input
                            type="text"
                            id="city"
                            value={city}
                            onChange={(e) => setCity(e.target.value)}
                            required
                        />
                        <label htmlFor="state">State</label>
                        <select
                            id="state"
                            value={state}
                            onChange={(e) => setState(e.target.value)}
                        >
                            <option value="" selected disabled hidden>
                                Alabama
                            </option>
                            {US_STATES.map((state) => (
                                <option key={state} value={state}>
                                    {state}
                                </option>
                            ))}
                        </select>
                        <label htmlFor="zipCode">Zip Code</label>
                        <input
                            type="text"
                            id="zipCode"
                            value={zipCode}
                            onChange={(e) => setZipCode(e.target.value)}
                            required
                        />
                    </fieldset>
                    <label htmlFor="department">Department</label>
                    <select
                        id="department"
                        value={department}
                        onChange={(e) => setDepartment(e.target.value)}
                        required
                    >
                        <option value="" selected disabled hidden>
                            Sales
                        </option>
                        {COMPANY_DEPT.map((department) => (
                            <option key={department} value={department}>
                                {department}
                            </option>
                        ))}
                    </select>
                    <button type="submit" onClick={openModal}>
                        Save
                    </button>
                </form>
                <Modal
                    isOpen={modalIsOpen}
                    onAfterOpen={afterOpenModal}
                    onRequestClose={closeModal}
                    style={customStyles}
                    contentLabel="Example Modal"
                >
                    <h2 ref={(_subtitle) => (subtitle = _subtitle)}>Hello</h2>
                    <button onClick={closeModal}>close</button>
                    <div>I am a modal</div>
                    <form>
                        <input />
                        <button>tab navigation</button>
                        <button>stays</button>
                        <button>inside</button>
                        <button>the modal</button>
                    </form>
                </Modal>
            </div>
        </>
    );
};

export default RegisterForm;
